import classes from "./Item.module.css";
import { useState, useRef } from "react";

const Item = (props) => {
  const [isEditing, setIsEditing] = useState(false);
  const titleRef = useRef(null);

  const deleteHandler = () => {
    props.onDelete(props.id);
  };

  const editHandler = (e) => {
    e.stopPropagation();
    titleRef.current.focus();
    setIsEditing(true);
  };

  const saveHandler = () => {
    titleRef.current.blur();

    if (titleRef.current.textContent.trim().length === 0) {
      deleteHandler();
    } else if (titleRef.current.textContent !== props.children) {
      props.onEdit(props.id, titleRef.current.textContent);
    }
    setIsEditing(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" || e.key === "Escape") {
      saveHandler();
    }
  };

  return (
    <li className={`${classes["task-item"]} ${props.even && classes.even}`}>
      <div
        onClick={editHandler}
        onKeyDown={handleKeyPress}
        contentEditable={true}
        suppressContentEditableWarning={true}
        onBlur={saveHandler}
        ref={titleRef}
        className={`${classes.title} ${isEditing && classes["edit-mode"]}`}
      >
        {props.children}
      </div>
      <span
        onClick={editHandler}
        className={`${classes.actions} ${classes.edit}`}
      >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
          {/* Font Awesome Pro 6.2.1 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2022 Fonticons, Inc. */}
          <path d="M471.6 21.7c-21.9-21.9-57.3-21.9-79.2 0L362.3 51.7l97.9 97.9 30.1-30.1c21.9-21.9 21.9-57.3 0-79.2L471.6 21.7zm-299.2 220c-6.1 6.1-10.8 13.6-13.5 21.9l-29.6 88.8c-2.9 8.6-.6 18.1 5.8 24.6s15.9 8.7 24.6 5.8l88.8-29.6c8.2-2.8 15.7-7.4 21.9-13.5L437.7 172.3 339.7 74.3 172.4 241.7zM96 64C43 64 0 107 0 160V416c0 53 43 96 96 96H352c53 0 96-43 96-96V320c0-17.7-14.3-32-32-32s-32 14.3-32 32v96c0 17.7-14.3 32-32 32H96c-17.7 0-32-14.3-32-32V160c0-17.7 14.3-32 32-32h96c17.7 0 32-14.3 32-32s-14.3-32-32-32H96z" />
        </svg>
      </span>
      <span
        onClick={deleteHandler}
        className={`${classes.actions} ${classes.delete}`}
      >
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512">
          {/* Font Awesome Pro 6.2.1 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2022 Fonticons, Inc. */}
          <path d="M290.7 57.4L57.4 290.7c-25 25-25 65.5 0 90.5l80 80c12 12 28.3 18.7 45.3 18.7H288h9.4H512c17.7 0 32-14.3 32-32s-14.3-32-32-32H387.9L518.6 285.3c25-25 25-65.5 0-90.5L381.3 57.4c-25-25-65.5-25-90.5 0zM297.4 416H288l-105.4 0-80-80L227.3 211.3 364.7 348.7 297.4 416z" />
        </svg>
      </span>
    </li>
  );
};

export default Item;
